# A function to split the value in a cell. 
Func.split <- function(data_ser){
  df <- data.frame(stringsAsFactors = FALSE)
  for(i in (1:length(data_ser))){
    x <- unlist(strsplit(data_ser[i],split = ","))
    df <- rbind(df, x,stringsAsFactors = FALSE)
  }
  attr(df,"names") <- c(1:length(x))
  return(df)
}

#Null replacement function
Func.NULL <- function(dataframe){
  for(i in (1:nrow(dataframe))){
    for(j in (1:ncol(dataframe))){
      if (dataframe[i,j]=="NULL") {
        dataframe[i,j] = NA
      }
    }
  }
  return(dataframe)
}


#convert string to numerical value
Func.strToNum <- function(dataframe){
  Numvector <- as.numeric(unlist(dataframe))
  empty_df <- data.frame()
  for(i in (1:nrow(dataframe))){
    for(j in (1:ncol(dataframe))){
      empty_df[i,j] = Numvector[i+nrow(dataframe)*(j-1)]
    }
  }
  attr(empty_df,"names") <- c(1:ncol(dataframe))
  return(empty_df)
}




